import { useState } from 'react';
import './App.css';

function App() {
  const initialValues = { username: "", email: "", password: "" }
  const [formValues, setformValues] = useState(initialValues);
  const handleChange = (e) => {
  const{ name, value }= e.target
  setformValues({...formValues, [name] : value })
  console.log(formValues);
  }
  const handleSubmit = (e) => {
   e.preventDefault();

  };


  return (
    <div className="container">
      <pre>{JSON.stringify(formValues, undefined, 1)}</pre>
      <form onSubmit={handleSubmit}>
        <h1>Login Form</h1>
        <div className="ui divider"></div>
        <div classname="ui form">
          <div className="field">
            <label>Username:- </label>
            <input type="text" name="username" placeholder="Username" value={formValues.username} onChange={handleChange}></input>
          </div>
          <div className="field1">
            <label className="asa">Email:- </label>
            <input type="text" name="email" placeholder="Email" value={formValues.email} onChange={handleChange}></input>
          </div>
          <div className="field">
            <label>Password:- </label>
            <input type="Password" name="password" placeholder="Password" value={formValues.password} onChange={handleChange}></input>
          </div>
          <button className="submit">Submit</button>
        </div>
      </form>
    </div>

  );
}

export default App;
