import { useState, useEffect } from 'react';
import './App.css';

function App() {
  const initialValues = { email: "", password: "" }
  const [formValues, setformValues] = useState(initialValues);
  const [formErrors, setformErrors] = useState({});
  const [IsSubmit, setIsSubmit] = useState(false);
  const handleChange = (e) => {
    const { name, value } = e.target
    setformValues({ ...formValues, [name]: value })
    console.log(formValues);
  }
  const handleSubmit = (e) => {
    e.preventDefault();
    setformErrors(validate(formValues));
    setIsSubmit(true);
  };

  useEffect(() => {
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && IsSubmit) {
      console.log(formValues);
    }
  }, [formErrors])

  const validate = (values) => {
    const errors = {}
    if (!values.email) {
      errors.email = "email is required"
    }
    if (!values.password) {
      errors.password = "password is required"
    }
    else if (values.password.length < 4) {
      errors.password = "password must be more than 4 characters"
    }
    else if (values.password.length > 8) {
      errors.password = "password must be less than 8 characters"

    }
    return errors
  }


  return (
    <div className="container">
      {Object.keys(formErrors).length === 0 && IsSubmit ? (<div className="success">Signed in Successfully</div>) : 
         <pre>{JSON.stringify(formValues, undefined, 2)}</pre>}
      <form onSubmit={handleSubmit}>
        <h1>Login Form</h1>
        <div className="ui divider"></div>
        <div classname="ui form">
          <div className="field1">
            <label className="asa">Email:- </label>
            <input type="text" name="email" placeholder="Email" value={formValues.email} onChange={handleChange}></input>
          </div>
          <p1>{formErrors.email}</p1>
          <div className="field2">
            <label>Password:- </label>
            <input type="Password" name="password" placeholder="Password" value={formValues.password} onChange={handleChange}></input>
          </div>
          <p1>{formErrors.password}</p1>
          <br></br>
          <button className="submit">Submit</button>
        </div>
      </form>
    </div>

  );
}

export default App;
