import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from "axios";
import { Link } from 'react-router-dom';

function ResetPage() {
    const initialValues = { Npass: "", Cnewpass: "" };
    const [formValues, setFormValues] = useState(initialValues);

    const [formErrors, setFormErrors] = useState({});
    const [isSubmit, setIsSubmit] = useState(false);


    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormValues({ ...formValues, [name]: value });
    };


    const handleSubmit = (e) => {
        e.preventDefault();
        setFormErrors(validate(formValues));
        setIsSubmit(true);
        axios.post("http://localhost:3001/login", {
            Npass: formValues.Npass,
            Cnewpass: formValues.Cnewpass,

        }).then((response) => {
            console.log(response)
            if (response.data.status === "success") {
                alert(formValues.email + " successfully Resetted");
            }
            else {
                console.log(response);
                alert('failed');
            }
        

        });

    };

    useEffect(() => {
        console.log(formErrors);
        if (Object.keys(formErrors).length === 0 && isSubmit) {
            console.log(formValues);

        }
    }, [formErrors]);
    const validate = (values) => {
        const errors = {};
        if (!values.Npass) {
            errors.Npass = "Password is required";
        } else if (values.Npass.length < 4) {
            errors.Npass = "Password must be more than 4 characters";
        } else if (values.Npass.length > 10) {
            errors.Npass = "Password cannot exceed more than 10 characters";
        }
        else if (values.Npass != values.Cnewpass)
            errors.Cnewpass = "Please Enter Same Password"
        return errors;

    };
    return (
        <div className="">
            <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.13.1/css/all.min.css" integrity="sha256-2XFplPlrFClt0bIdPgpz8H7ojnk10H69xRqd9+uTShA=" crossorigin="anonymous" />
            <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.1.3/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-1BmE4kWBq78iYhFldvKuhfTAU6auU8tT94WrHftjDbrCEXSU1oBoqyl2QvZ6jIW3" crossorigin="anonymous"></link>

            <section className="text-center bg-warning m-2 mt-2 p-2 d-flex aligns-items-center justify-content-md-center ">

                <h4 className="p-2 mt-5">WELCOME TO PASSWORD RESET PAGE</h4>

            </section>
            <div class="container mt-5 ">
                <div className="row aligns-items-center justify-content-center">
                    <div className="col-sm-4">
                        <pre>{JSON.stringify(formValues, undefined, 2)}</pre>
                        <form onSubmit={handleSubmit}>
                            <div className="field mb-3 m-2 text-center form-group">
                                <label>New Password</label>
                                <input
                                    className='form-control text-center'
                                    type="password"
                                    name="Npass"
                                    placeholder="New Password"
                                    value={formValues.Npass}
                                    onChange={handleChange} />
                            </div>
                            <p>{formErrors.Npass}</p>
                            <div className="field mb-3 m-2 text-center form-group">
                                <label>Confirm New Password</label>
                                <input
                                    className='form-control text-center'
                                    type="password"
                                    name="Cnewpass"
                                    placeholder="Confirm New Password"
                                    value={formValues.Cnewpass}
                                    onChange={handleChange} />
                            </div>
                            <p>{formErrors.Cnewpass}</p>

                            <div className='mb-3 m-2 text-center form-group'>
                                <button className="btn btn-warning text-center">SUBMIT</button>
                            </div>


                        </form>

                    </div>
                </div>




            </div >

        </div>





    )
}

export default ResetPage;
