import React from "react";
import { useState, useEffect } from "react";


function Forget (){
    const initialValues = {security_question: " " };
    const [formValues, setFormValues] = useState(initialValues);

    const [formErrors, setFormErrors] = useState({});
    const [isSubmit, setIsSubmit] = useState(false);


    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormValues({ ...formValues, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setFormErrors(validate(formValues));
        setIsSubmit(true);
        

    };
    useEffect(() => {
        console.log(formErrors);
        if (Object.keys(formErrors).length === 0 && isSubmit) {
            console.log(formValues);
        }
    }, [formErrors]);
    const validate = (values) => {
        const errors = {};
        if (!values.security_question) {
            errors.security_question = "select your sequrity question";
        }
        return errors;
    };  

    return(
        <div>
        <div className="field mb-3 m-2 text-center form-group">
        <label>Select security question:- </label>
        <select
            name="security_question"
            type="option"
            placeholder="security question">
                onChange={handleChange}
                value={formValues.security_question}
            <option value="which is your favourite place">which is your favourite place</option>
            <option value="Surname of favourite teacher">surname of favourite teacher</option>
            <option value="First pet name">First pet name</option>
        </select>
        </div>
        <p>{formErrors.security_question}</p>
        </div>

    )
}



export default Forget;
